TableView - scripting support

example:
"stage":
  [
    {
      "name":"simple-table",
      "type":"TableView",
      "background-color": [0.5,0.5,0,1],
      "parent-origin": "CENTER",
      "size":[400,500,1],
      "rows": 4,
      "columns":4,
      "cell-padding": [10, 5],
      "layout-animation-duration": 0.5,
      "layout-rows":
      {
        "0": { "policy": "fixed", "value": 40 },
        "1": { "policy": "relative", "value": 0.33 },
        "2": { "policy": "fixed", "value": 120 }
      },
      "layout-columns":
      {
        "1": { "policy": "fixed", "value": 150 },
        "2": { "policy": "relative", "value": 0.35 },
        "3": { "policy": "relative", "value": 0.15 }
      },
      "actors":
      [
        {
          "name":"magazine-0",
          "type":"ImageActor",
          "image":
          {
            "filename": "{DALI_IMAGE_DIR}magazine_0.jpg"
          },
          "custom-properties":
          {
            "cell-indices": [0,0],
            "row-span":4,
            "column-spam":1
          }
        },
        {
          "name":"magazine-1",
          "type":"ImageActor",
          "image":
          {
            "filename": "{DALI_IMAGE_DIR}magazine_1.jpg"
          },
          "custom-properties":
          {
            "cell-indices":[1,1],
            "row-span":3
          }
        },
        {
          "name":"magazine-2",
          "type":"ImageActor",
          "image":
          {
            "filename": "{DALI_IMAGE_DIR}magazine_2.jpg"
          },
          "custom-properties":
          {
            "cell-indices":[2,2]
          }
        },
        {
          "name":"magazine-3",
          "type":"ImageActor",
          "image":
          {
            "filename": "{DALI_IMAGE_DIR}magazine_3.jpg"
          },
          "custom-properties":
          {
            "cell-indices":[3,2],
            "column-span": 2
          }
        }
      ]
    }
  ]

